layer.onover:
set(iszoom, true);
set(startzoom, true);
copy(copy_mousefovchange, control.mousefovchange);
copy(copy_touchzoom, control.touchzoom);
set(control.mousefovchange, 0);
set(control.touchzoom, false);

layer.onout:
set(iszoom, false);
copy(control.mousefovchange, copy_mousefovchange);
copy(control.touchzoom, copy_touchzoom);

scene.action:
			if(iszoom,
				if(wheeldelta_touchscale GT 0,
					
					if(startzoom,
						set(startzoom,false);
						copy(start_wheeldelta_touchscale, wheeldelta_touchscale);
						copy(start_imagescale, layer[video].scale);
					  );

					div(tmp, wheeldelta_touchscale, start_wheeldelta_touchscale);
					mul(layer[video].scale, start_imagescale, tmp);
					if(layer[video].scale GT 1.5,set(layer[video].scale,1.5));
					if(layer[video].scale LT 0.1,set(layer[video].scale,0.1));
				  ,
					
					mul(sit,get(wheeldelta),0.05);
					mul(sit,layer[video].scale);
					add(layer[video].scale,sit);
					if(layer[video].scale GT 2.0,set(layer[video].scale,2.0));
					if(layer[video].scale LT 0.1,set(layer[video].scale,0.1));
				  );
			);

